import { Card, CardContent, TextField } from "@material-ui/core";
import { Field, Form, Formik, FormikConfig, FormikValues } from "formik";
import { CheckboxWithLabel } from "formik-material-ui";
import React, { Children, useState } from "react";

export const FormDisplay = () => {
  return (
    <div>
      <Card>
        <CardContent>
          <FormikStepper
            initialValues={{
              firstname: "",
              lastname: "",
              age: "",
              email: "",
              password: "",
              male: false,
              female: false,
              contact: "",
              description: "",
            }}
            onSubmit={async (values) => {
              console.log("values", values);
            }}
          >
            <FormikStep>
              <Field
                type="text"
                label="First Name"
                name="firstname"
                component={TextField}
              />
              <Field
                type="text"
                label="Last Name"
                name="lastname"
                component={TextField}
              />
              <Field
                type="number"
                label="Age"
                name="age"
                component={TextField}
              />
            </FormikStep>
            <FormikStep>
              <Field
                type="text"
                label="Email"
                name="email"
                component={TextField}
              />
              <Field
                type="text"
                label="Password"
                name="password"
                component={TextField}
              />
            </FormikStep>
            <FormikStep>
              <Field
                type="checkbox"
                Label={{ label: "Female" }}
                name=" female"
                component={CheckboxWithLabel}
              />
              <Field
                type="checkbox"
                Label={{ label: "Male" }}
                name="male"
                component={CheckboxWithLabel}
              />
              <Field
                type="number"
                label="Contact"
                name="contact"
                component={TextField}
              />
              <Field
                type="text"
                label="Description"
                name="description"
                component={TextField}
              />
            </FormikStep>
          </FormikStepper>
        </CardContent>
      </Card>
    </div>
  );
};

export const FormikStepper = ({
  children,
  ...props
}: FormikConfig<FormikValues>) => {
  const ChildrenArray = React.Children.toArray(children);

  const [step, setstep] = useState(0);

  const currentchildren = ChildrenArray[step];

  return (
    <Formik {...props}>
      <Form>{currentchildren}</Form>
    </Formik>
  );
};
export interface Proptypestep
  extends Pick<FormikConfig<FormikValues>, "children" | "validationSchema"> {}

export const FormikStep = ({ children }: Proptypestep) => {
  return <>{children}</>;
};
